import axios from 'axios';
import { useContext, useState } from 'react';
import SideBar from '../../components/SideBar/SideBar';
import { Context } from '../../context/Context';
import './settings.css';

const Settings = () => {
    const { user } = useContext(Context);
    const [file, setFile] = useState(null);
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        const updatedUser = {
            userId: user._id,
            username,
            email,
            password,
        };
        if (file) {
            const data = new FormData();
            const filename = Date.now() + file.name;
            data.append("name", filename);
            data.append("file", file);
            updatedUser.profilePic = filename;
            try {
                await axios.post("/upload",data)
            } catch (err) {

            }
        }
        try {
            await axios.put("/users"+user._id,updatedUser);

        } catch (error) {

        }
    }
    return (
        <div className="settings">
            <div className="settingsWrapper">
                <div className="settingsTitle">
                    <span className="settingsUpdateTitle">Update your Account</span>
                    <span className="settingsDeleteTitle">Delete your Account</span>
                </div>
                <form className="settingsForm">
                    <label>Profile Picture</label>
                    <div className="settingsPP">
                        <img src={user.profilePic} alt="pro" />
                        <label htmlFor="fileInput">
                        <i className="settingsPPIcon fas fa-user-circle"></i>
                        </label>
                        <input type="file" id="fileInput" style ={{display:"none"}} onChange={e=>setFile(e.target.files[0])} />
                    </div>
                    <label >Username</label>
                    <input type="text" className="usrname" placeholder={user.username} onChange = {e=>setUsername(e.target.value)} />
                    <label >Email</label>
                    <input type="email" className="email" placeholder= {user.email} />
                    <label >Password</label>
                    <input type="password" className="password" />
                    <button className="settingSubmit">Update</button>
                </form>
            </div>
            <SideBar />
        </div>
    );
}

export default Settings;
